class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int pre = colors[0], count = 1, res = 0;
        for (int i = 1; i < n + k - 1; i++) {
            if (colors[i % n] != pre) {
                count++;
                if (count == k) {
                    res++;
                    count--;
                }
                pre = colors[i % n];
            } else {
                if (i >= n) {
                    break;
                }
                pre = colors[i % n];
                count = 1;
            }
        }
        return res;
    }
}
