class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> dp(n, 1);

        for(int i = n-2; i>=0; i--) {
            if(colors[i] != colors[i+1])
                dp[i] += dp[i+1];
        }    

        int ans = 0;

        for(int i = 0; i<n; i++) {
            int curr = dp[i];
            if((i + curr) == n && colors[n-1] != colors[0])
                curr += dp[0];

            ans += (curr >= k);
        }

        return ans;
    }
};
