class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0;
        bool prev=0;
        for(int i=0;i<colors.size();i++){
          bool x=colors[i],t=1;
          if(prev==1){
              if(colors[(i+k-1)%colors.size()]!=colors[(i+k-2)%colors.size()])
              t=1;
              else
              t=0;
          }
          else{
          for(int j=1;j<k;j++){
               if(colors[(i+j)%colors.size()]==x){
                   t=0;
               }
              x=!x;
          }
          }
          if(t){
           ans++;
           prev=1;
          }
          else{
            prev=0;
          }
        }
        return ans;
    }
};
