class Solution {
public:
    int numberOfAlternatingGroups(vector<int> &colors, int k) {
        int N = colors.size();
        if (k == 1) {
            return N; 
        }
        int groupCount = 0;
        int currentLength = 1;
        int index = 1;
        while (index < N + k - 1) {
            int current = index % N;
            int previous = (index - 1) % N;
            if ((colors[current] ^ colors[previous]) == 1) {
                currentLength++;
            } else if((colors[current] ^ colors[previous]) == 0){
                currentLength = 1;
            }

            if (currentLength >k-1) {
                groupCount++;
            }

            index++;
        }
        return groupCount;
    }
};
