class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        c = colors
        c = c+c
        # print(c)
        n = len(colors)

        i = 0
        j = -1
        res = 0

        while i<n:
            if j<i:
                j = i
            while (j+1-i)+1 <= k and c[j+1]!=c[j] and j+1<2*n:
                j+=1
            # print(c, i,j)
            if j-i+1 >=k:
                res+=1

            i+=1


        return res
