class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int count = 0;
        int n = colors.size();
        vector<int> extended_colors = colors;
        extended_colors.insert(extended_colors.end(), colors.begin(), colors.begin() + k - 1);
        int temp = 1;
        int m = extended_colors.size();

        for (int i = 1; i < m; ++i) {
            if (extended_colors[i] != extended_colors[i - 1]) {
                temp++;
            } else {
                temp = 1;
            }
            if (temp >= k) {
                count++;
            }
        }
        return count;
    }
};
