class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        bool flag=false;
        for (int start = 0; start < n; start++){
            if(flag==false){
                bool isValidGroup=true;
                for (int i = 0; i < k; ++i) {
                    int current = (start + i) % n;
                    int next = (start + i + 1) % n;
                    if (i != k - 1 && colors[current] == colors[next]) {
                        isValidGroup = false;
                         break;
                    }
            }
            if (isValidGroup) {
                count++;
                flag=true;
            }
            }
            else{
                int last=(start+k-1)%n;
                if(colors[last]!=colors[last-1==-1?n-1:last-1]){
                    count++;
                }
                else{
                    flag=false;
                }
            }
        }

        return count;
    }
};
