class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        if(k > colors.size())   return 0;
        int ans = 0;
        int n = colors.size();
        int len = 1;
        for(int i = 1; i <= n + k - 2; i++){
            if(len >= k)    ans++;
            if(colors[i % n] != colors[(i-1) % n])    len++;
            else    len = 1;
        }
        if(len >= k)    ans++;
        return ans;
    }
};
