class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> c=colors;
        c.insert(c.end(),colors.begin(),colors.begin()+k-1);
        int ans=0;
        int temp=1;
        for(int i=1;i<c.size();i++){
            if(c[i]!=c[i-1])temp++;
            else{
                temp=1;
            }
            if(temp>=k)ans++;
        }
        return ans;
    }
};
