class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
       int n=colors.size();
       vector<int> v;
       v=colors;
       int c=0;
       for(int i=0;i<min(k,(int)colors.size());i++)
            v.push_back(colors[i]);
      // map<string,int> m;
       vector<int> pdiff(v.size()+1,0);

       pdiff[0]=0;
       for(int i=1;i<v.size();i++){
        pdiff[i]+=(pdiff[i-1]+abs(v[i]-v[i-1]));
       }

       for(int i=k;i<v.size();i++){
        if((pdiff[i]-pdiff[i-k+1])==(k-1))
            c++;
       }

       return c;

    }
};
