class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k > n) return 0; 

        int res = 0;
        int cnt = 1; 

        for (int i = 1; i < n + k - 1; ++i) {
            if (colors[i % n] != colors[(i - 1) % n]) {
                cnt++;
            } else {
                cnt = 1; 
            }

            if (cnt >= k) {
                res++;
                cnt--; 
            }
        }

        return res;
    }
};
