class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>num=colors;
        for(int i=0;i<colors.size();i++){
            num.push_back(colors[i]);
        }
        int ans=0;
        int n=colors.size();
        stack<int>q;
        for(int i=0;i<n+k-1;){
            if(q.empty()){
                q.push(num[i]);
                i++;
            }
            else{
                if(q.top()!=num[i]){
                     q.push(num[i]);
                    i++;
                }
                else{
                   while(q.size()!=0)q.pop();
                    q.push(num[i]);
                    i++;
                }                
            }

             if(q.size()>=k){
                  cout<<i<<" ";
                 ans++;
             }
        }
        cout<<endl;
        return ans;
    }
};
