class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i = 0, j = 0, prev = -1, ans = 0;

        for (int i = 0; i < k - 1; i++)
            colors.push_back(colors[i]);

        while (i < colors.size())
        {
            int cnt = 0, prev = -1;
            while (i < colors.size() && cnt < k)
            {
                if (prev == -1)
                    prev = colors[i];

                else if (colors[i] == prev)
                    break;

                prev = colors[i];
                cnt++, i++;
            }

            if (cnt != k)
                continue;

            else
            {
                ans++;
                while (i < colors.size() && colors[i] == (k % 2 ? colors[i - k + 1] : 1 - colors[i - k + 1]))
                    i++, ans++;
            }
        }

        return ans;

    }
};
