class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)

        e = colors + colors[:k-1]  
        print(e)
        count = 0
        left=right=0
        curlen=0
        while right<len(e):
            if curlen>=k:
                count+=1
            if (right-left)%2==0 and e[right]==e[left]:
                right+=1
                curlen+=1
            elif (right-left)%2==1 and e[right]!=e[left]:
                right+=1
                curlen+=1
            else:
                left=right
                curlen=0
        if curlen>=k:
            count+=1

        return count
