class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int nf = 0;
        int i = 0;
        int j = 1;
        int n = colors.size();

        while (j < n + k && i < n) {
            int tj = j;
            int te = tj % n;

            while (colors[te] != colors[(te - 1 + n) % n] && tj < n + k - 1) {
                tj++;
                te = tj % n;
            }

            nf += max(tj - i - k + 1, 0);
            i = tj;
            j = i + 1;
        }

        return nf;
    }
};
