class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        ans = 0
        length = 0
        if len(colors) < k:
            return ans
        double_colors = colors + colors
        for i in range(1, len(double_colors)):
            if i == len(colors):
                ans = 0
            if double_colors[i] != double_colors[i-1]:
                length += 1
            else:
                length = 1
            if length >= k:
                ans += 1
        return ans
