/**
 * @param {number[]} colors
 * @param {number} k
 * @return {number}
 */
var numberOfAlternatingGroups = function(colors, k) {
     let groups = [];
    let lastColor = colors[0];
    let currentGroup = 1;
    let isSame = false;

    for (let i = 1; i < colors.length; i++) {
        if (colors[i] === lastColor) {
            groups.push(currentGroup);
            currentGroup = 1;
            isSame = true;
        } else {
            currentGroup++;
        }
        lastColor = colors[i];
    }
    groups.push(currentGroup);

    if (groups.length === 1 && groups[0] === colors.length && colors[0] !== colors[colors.length - 1] && !isSame) {
        return colors.length;
    }

    if (colors[0] !== colors[colors.length - 1] && groups.length > 1) {
        let lastGroup = groups.pop();
        groups[0] += lastGroup;
    }

    let result = 0;
    for (let group of groups) {
        result += Math.max(0, group - (k - 1));
    }

    return result;
};
