class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        alt=[0]*n
        for i in range(n):
            alt[i] = colors[i]!=colors[(i+1) % n]
        currentValid = True

        for i in range(k-1):
            if not alt[i]:
                currentValid=False
                break

        if currentValid:
            c=1
        else:
            c=0
        for i in range(1,n):
            if currentValid:
                currentValid=alt[(i+k-2)%n] and (k==2 or currentValid)
            else:
                currentValid=True
                for j in range(k-1):
                    if not alt[(i+j)%n]:
                        currentValid=False
                        break
            if currentValid:
                c+=1

        return c
