class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int ans = 0;
        if (k == 1) return n;
        int l = 1;
        int i = 1;
        while (i < n + k - 1) {
            int curr = i % n;
            int prev = (i - 1) % n;
            if (colors[curr] != colors[prev]) {
                l++;
            } else {
                l = 1;
            }

            if (l >= k) {
                ans++;
            }

            i++;
        }

        return ans;
    }
}
