class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& co, int k) {


        int n=co.size();
        for(int i=0;i<min(n-1,k-1);i++)co.push_back(co[i]);

        int c=1;
        int ans=0;
       // cout<<co[0]<<" ";
        for(int i=1;i<co.size();i++){
              // cout<<" "<<co[i]<<" ";
            if(co[i]!=co[i-1]){
                c++;
            }
            else 
                c=1;

            if(c==k){

                ans++,c--;
            }


        }
      //  cout<<endl;
        return ans;
    }
};
