class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        int ans = 0;

        int i = 0, j = 1;
        int d = 1;

        while(j < k+n-1){
            if(c[j%n] != c[(j - 1 + n) % n]){
                d++;
            } else {
                d = 1;
            }

            if(d >= k){
                ans++;
            }
            j++;
        }
        return ans;
    }
};
