class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int cnt = 0, start = 0 , end = 1;
        int n = colors.size();

        for (; end < n + k && start < n; ++end) {
            int curr = end % n;
            while (colors[curr] != colors[(curr - 1 + n) % n] && end < n + k - 1) {
                ++end;
                curr = end % n;
            }
            cnt += max(end - start - k + 1, 0);
            start = end;
        }

        return cnt;
    }
};
