class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int count = 0;
        int i = 0, j = 1;
        int size = colors.size();

        while (j < size + k && i < size) {
            int temp = j;
            int temp2 = temp % size;

            while (colors[temp2] != colors[(temp2 - 1 + size) % size] && temp < (size + k - 1)) {
                temp++;
                temp2 = temp % size;
            }

            count += max(temp - i - k + 1, 0);
            i = temp;
            j = i + 1;
        }

        return count;
    }
};
