class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<n;i++) colors.push_back(colors[i]);

        int ans=0;
        vector<int> dp(2*n);
        for(int i=2*n-1;i>=0;i--) {
            dp[i]=1;
            if (i<2*n-1 && colors[i+1]!=colors[i]) dp[i]=1+dp[i+1];
            if (i <= n-1 && dp[i]>=k) ans++;
        }
        return ans;


    }
};
