class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& snums, int k) { 
        int sn = snums.size(); 
        int scnt = 0; 

        if (k == 1) { 
            return sn; 
        } 

        int alength = 1; 
        int i = 1; 

        while (i < sn + k - 1) { 
            int scur = i; 
            int sprev = i - 1; 

            if (scur >= sn) scur -= sn; 
            if (sprev >= sn) sprev -= sn; 

            if (snums[scur] != snums[sprev]) ++alength; 
            else  alength = 1;

            if (alength >= k)  ++scnt; 
            ++i; 
        } 

        return scnt; 
    } 
};
