class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
  int num_found = 0;
    int i = 0;
    int j = 1;

    while (j < colors.size() + k && i < colors.size()) {
        int temp_j = j;
        int temp_end = temp_j % colors.size();

        while (colors[temp_end] != colors[(temp_end - 1 + colors.size()) % colors.size()] && (temp_j < colors.size() + k - 1)) {
            temp_j++;
            temp_end = temp_j % colors.size();
        }

        num_found += max(temp_j - i - k + 1, 0);
        i = temp_j;
        j = i + 1;
    }
    return num_found;
    }
};
