class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int ans = 0;
        int n = colors.length;
        for(int i=1,j=0;i<n+k-1;i++){
            if(colors[i%n] == colors[(i-1)%n]){
                j = i;
            }
            if(i - j == k-1){
               // System.out.println(i+" "+j);
                ans++;
                j++;
            }
        }
        //System.out.println("---");
        return ans;
    }
}
