class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        int cnt = 0;
        int s = 1;
        int i =0;
        while(1){
            if(i==n-1)break;
            if(a[i]!=a[i+1]){
                s++;
                i++;
            }
            else{
                i++;
                break;
            }
        }
        int j = n-1;
        int e = 1;
        while(1){
            if(j == 0)break;
            if(a[j]!=a[j-1]){
                e++;
                j--;
            }
            else {
                j--;
                break;
                }
        }
        if(a[0]!=a[n-1]&&s==n){
            if(k<=n)return n;
        }
        if(s==n&&a[0]==a[n-1]){
            if(k<=s)return s-k+1;
        }
        if(a[0]!=a[n-1]){
            if(s+e>=k)cnt += (s+e-k+1);
        }
        else{
            if(s>=k)cnt+=(s-k+1);
            if(e>=k)cnt+=(e-k+1); 
        }
        int sz = 1;
        if(j<=i)return cnt;
        while(1){
            if(i==j){
                if(sz>=k)cnt+=(sz-k+1);
                break;
            }
            if(a[i]!=a[i+1]){
                sz++;
            }
            else{
                if(sz>=k)cnt+=(sz-k+1);
                sz = 1;
            }
            i++;
        }
        return cnt;
    }
};
