class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

            if(k>colors.size())
             return(0);

                 int i;
             for(i=0;i<k-1;i++){
                colors.push_back(colors[i]);
             }


            queue<int>q;
             for(i=0;i<colors.size()-1;i++){
                 if(colors[i]==colors[i+1]){
                  q.push(i);

                 }
             }

             int l=0,r=k-1;
             int count=0;
            //cout<<"size "<<colors.size()<<endl;
             while(r<colors.size()){


               //   cout<<l<<"  "<<r<<"  "<<q.front()<<endl;
                  if(q.front()>=r||q.empty())
                   count++;

                r++;
                l++;
                if(!q.empty()&&q.front()<l)
                 q.pop();


             }

             return(count);

    }
};
