class Solution:
    def numberOfAlternatingGroups(self, nums: List[int], k: int) -> int:
        n = len(nums)
        ans = 0
        if nums[0] == nums[-1]:
            cnt = 1
            for i in range(1, n):
                if nums[i] != nums[i - 1]:
                    cnt += 1
                else:
                    ans += max(0, cnt - k + 1)
                    cnt = 1
            ans += max(0, cnt - k + 1)
            return ans
        cnt = 2
        for s in range(1, n):
            if nums[s] != nums[s - 1]:
                cnt += 1
            else:
                break
        # print(s)
        if s == n - 1 and nums[s] != nums[s-1]:
            return n
        e = None
        for e in range(n - 2, s - 1, -1):
            if nums[e] != nums[e + 1]:
                cnt += 1
            else:
                break
        ans += max(cnt - k + 1, 0)
        cnt = 1
        if e == None:
            return ans
        # print(e)
        for i in range(s, e + 1):
            if nums[i] != nums[i - 1]:
                cnt += 1
            else:
                ans += max(0, cnt - k + 1)
                cnt = 1
        ans += max(0, cnt - k + 1)
        return ans
