class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        arr.insert(arr.end(), arr.begin(), arr.begin() + k - 1);
        int n = size(arr);
        int cnt = 0, inv = 0;
        for(int i = 1; i < k; i++){
            if(arr[i] == arr[i - 1]){
                inv++;
            }
        }
        cnt += (inv == 0);
        for(int i = k; i < n; i++){
            if(arr[i - k] == arr[i - k + 1])
                inv--;
            if(arr[i] == arr[i - 1])
                inv++;
            cnt += (inv == 0);
        }
        return cnt;
    }
};
