class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        string s;
        int n = v.size();
        for(int i = 0;i < n;i++){
            if(v[i] == 0){
                s.push_back('0');
            }
            else{
                s.push_back('1');
            }
        }

        for(int i = 0;i < k-1;i++){
            if(v[i] == 0){
                s.push_back('0');
            }
            else{
                s.push_back('1');
            }
        }


        string g, h;
        for(int i = 0;i < k;i++){
            if(i&1){
                g.push_back('0');
                h.push_back('1');
            }
            else{
                g.push_back('1');
                h.push_back('0');
            }
        }

        int ans = 0;
        auto cnt = [&](string &s, string &h){
            string g = h + "*" + s;
            int n = g.size();
            vector<int> pi(n);
            for(int i = 1;i < n;i++){
                int j = pi[i-1];
                while(j > 0 && g[i] != g[j]){
                    j = pi[j-1];
                }
                if(g[i] == g[j]) j++;
                pi[i] = j;

                if(pi[i] == k) ans++;
            }
        };

        cnt(s, g);
        cnt(s, h);

        return ans;
    }
};
