class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& r, int k) {
        int n = r.size();
        int c = 0;

        if (k == 1) {
            return n;
        }

        vector<int> e = r;
        e.insert(e.end(), r.begin(), r.end());

        int l = 1;

        for (int i = 1; i < e.size(); ++i) {
            if (e[i] != e[i - 1]) {
                ++l;
            } else {
                l = 1;
            }

            if (l >= k && i < n + k - 1) {
                ++c;
            }
        }

        return c;
    }
};
