public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.Length;
        List<int> comb = new List<int>(colors);
        for(int i = 0;i < n; i++) comb.Add(colors[i]);
        int l = 0,r = 0;
        int count = 0;
        int ans = 0;
        while(r < n + k - 1){
            if(r >= k - 1){
                ans += count == 0 ? 1 : 0;
                if(comb[l + 1] == comb[l])count--;
                l++;
            }
            if(comb[r+1] == comb[r]) count++;
            r++;
        }
        return ans;
    }
}
