class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(),colors.begin(),colors.begin()+k);
        int n=colors.size(),c=0;
        vector<int> aa(n+1,0);
        for(int j=1;j<n;j++){
            aa[j]+=aa[j-1]+(colors[j]^colors[j-1]);
        }
        k--;
        for(int j=k+1;j<n;j++){
            if(aa[j]-aa[j-k]==k) c++;
        }
        return c;
    }

};
