class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length, ans = 0, i = 0, j = 1;
        while (i < n && j < n + k) {
            int prev = j;
            int last = prev % n;
            while (prev < n + k - 1 && colors[last] != colors[(last - 1 + n) % n] ) {
                prev++;
                last = prev % n;
            }
            if(prev - i - (k - 1) > 0) ans += (prev - i - k + 1);
            j = prev + 1;
            i = prev;
        }
        return ans;
    }
}
