class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors,int k) {
        long long n=colors.size();
        vector<vector<long long>> dp(n+k,vector<long long>(2,0));
        if(colors[0]==0){
            dp[0][0]=1;
        }
        else{
            dp[0][1]=1;
        }
        for(int i=1;i<n+k;i++){
            int i2=i%n;
            int i3=(i+n-1)%n;
                // cout<<i2<<endl;
            if(colors[i2]==0){
                // cout<<dp[i2][1]+1<<endl;
                dp[i2][0]=dp[i3][1]+1;
            }
            else{
                dp[i2][1]=dp[i3][0]+1;
            }
        }
        long long ans=0;
        for(int i=0;i<n+k;i++){
            for(int j=0;j<2;j++){
                if(dp[i][j]>=k){
                    ans++;
                }
            }
        }
        return ans;
    }
};
