class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0; i < n; i++){
            colors.push_back(colors[i]);
        }
        int i = 0,j = 1,ans = 0,curr = 0;
        while(j<2*n-1){
            if(i >= n) break;
            if(colors[j-1]==colors[j]){
                i = j;
            }
            if(j-i+1 == k) {
                ans++;
                i++;
            }
            j++;
        }
        return ans;
    }
};
