class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (n < k) return 0;
        vector<int> ex(colors.begin(), colors.end());
        ex.insert(ex.end(), colors.begin(), colors.begin() + k - 1);
        int ans = 0,count = 1;
        for (int i = 1; i < ex.size(); ++i) {
            count = (ex[i] != ex[i - 1]) ? count + 1 : 1;
            if (count >= k) {
                ans++;
            }
        }

        return ans;
    }
};
