class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ans=0,n=v.size(),y=1;
        for(int i=0;i<k-1;i++) v.push_back(v[i]);
        int x=0;
        for(int i=1;i<n+k-1;i++){
            if(v[i]!=v[i-1]) y++;
            else y=1;
            if(y>=k) ans++;
        }
        return ans;
    }
};
