class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int ans=0;
        int i,j;
        i=0;
        bool yes=1;
        for(i=0;i<k-1;i++){
            c.push_back(c[i]);
        }
        int n=c.size();
        for(i=1;i<k;i++){
            if(c[i]==c[i-1]){
                yes=0;
                break;
            }
        }
        if(yes){ans++;j=i;}
        else{j=i;}
        while(j<n){
            if(!yes){
                if(j+k <= n){
                    yes=1;
                    for(i=j+1;i<j+k;i++){
                        if(c[i]==c[i-1]){
                            yes=0;
                            j=i;
                            break;
                        }
                    }
                    if(yes){
                    j=j+k;
                    ans++;}
                }
                else{
                    break;
                }
            }
            else{
                if(c[j]!=c[j-1]){
                    ans++;

                }
                else{
                    yes=0;
                    j--;
                }
                j++;
            }
        }
        return ans;
    }
};
