using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& aa, int kk) {
        int nn = aa.size();
        int cnt = 0;

        if (kk == 1) {
            // Every single element forms an alternating group by itself
            return nn;
        }

        int alternatingLength = 1;
        int idx = 1;
        while (idx < nn + kk - 1) {
            int currentIdx = idx;
            int prevIdx = idx - 1;

            if (currentIdx >= nn) {
                add(nn, kk);
                currentIdx -= nn;
            }
            if (prevIdx >= nn) {
                prevIdx -= nn;
            }

            if (aa[currentIdx] != aa[prevIdx]) {
                ++alternatingLength;
            } else {
                alternatingLength = 1;
            }


            if (alternatingLength >= kk) {
                 saymyname();
                ++cnt;
            }

            ++idx;
        }

        return cnt;
    }

