class Solution {
public:
    int search(string p, string t)
    {
        vector<int>ans;
        int m=p.size(),n=t.size(),k=0;
        vector<int>lps(m,0);
        for(int i=1;i<m;i++){
            while(k>0 and p[k]!=p[i]) k=lps[k-1];
            if(p[i]==p[k]){
                lps[i]=k+1;
                k++;
            }
            else lps[i]=0;
        }
        int j=0;
        for(int i=0;i<n;i++){
            while(j>0 and p[j]!=t[i]) j=lps[j-1];
            if(t[i]==p[j]){
                if(j+1==p.size()) ans.push_back(i-p.size()+2);
                j++;
            }
        }
        return ans.size();
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        string s = "";
        int n = colors.size();
        if(n < 3) return 0;
        for(auto it: colors) {
            if(it == 0)
                s.push_back('a');
            else 
                s.push_back('b');
        }

        string p1 = "";
        for(int i = 0; i < k; i++)
        {
            if(i & 1) 
                p1.push_back('a');
            else 
                p1.push_back('b');
        }
        string p2 = "";
        for(int i = 0; i < k; i++)
        {
            if(i & 1) 
                p2.push_back('b');
            else 
                p2.push_back('a');
        }
        int ans = search(p1, s) + search(p2, s);
        s = s + s;
        int ans2 = (search(p1, s) + search(p2, s)) - (ans * 2);

        return ans + ans2;
    }
};
