#define ll long long
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n=colors.size(); 
        int cnt=0, sz=1; 
        int i = 1, N=n+k-1;
        for (int i=1;i<N;i++) { 
            if (colors[i%n] != colors[(i-1)%n]) { 
                sz++; 
            } 
            else{ 
                sz=1; 
            } 
            if(sz>=k) { 
                cnt+=1; 
            } 
        } 
        return cnt; 
    } 
};
