class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int javab = 0;
        int n = colors.length;

        for (int i = 0, j = 1; j < n + k && i < n; ) {
            int ind = j;
            int end = ind % n;


            while (colors[end] != colors[(end - 1 + n) % n] && ind < n + k - 1) {
                ind++;
                end = ind% n;
            }
            int num = ind - i - k + 1;
            javab += Math.max(num, 0);
            i = ind;
            j = i + 1;
        }

        return javab;
    }
}
