class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        dp = [0] * n * 2
        dp[0] = 1
        colors += colors
        for i in range(1,2*n):
            if colors[i] != colors[i-1]:
                dp[i] = dp[i-1] + 1
            else:
                dp[i] = 1

        ans = 0
        for i in range(n+k-1):
            if dp[i] >= k:
                ans += 1

        return ans
