#define ll long long

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        ll n = a.size();

        for(ll i = 0; i<n; i++){
            a.push_back(a[i]);
        }

        ll ans = 0;

        ll l = 0;

        for(ll r = 1; r<a.size(); r++){
            if(a[r - 1] == a[r]){
                l = r;
            }

            if(l + k - 1 == r){
                ans++;
                l++;
            }

            if(l >= n){
                break;
            }
        }

        return ans;
    }
};
