class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int cnt = 0; 

        if (k == 1) { 
            return n; 
        } 

        int altLength = 1; 
        int i = 1; 
        while (i < n + k - 1) { 
            int current = i; 
            int previous = i - 1; 

            if (current >= n) { 
                current -= n; 
            } 
            if (previous >= n) { 
                previous -= n; 
            } 

            if (colors[current] != colors[previous]) { 
                ++altLength; 
            } else { 
                altLength = 1; 
            } 

            if (altLength >= k) { 
                ++cnt; 
            } 

            ++i; 
        } 

        return cnt; 
    }

};
