class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(); 
        int ans = 0; 

        if (k == 1) return n; 
        for(int i=0;i<k-1;i++){
            colors.push_back(colors[i]);
        }

        int alternating_length = 1; 
        for(int i=1;i<colors.size();i++) {  
            if (colors[i] != colors[i-1]) ++alternating_length; 
            else alternating_length = 1; 
            if (alternating_length >= k) ans++;
        } 

        return ans; 
    }
};
