class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        int i = 0, j = 1;
        while(j < n + k && i < n)
        {
            int t = j;
            int end = t % n;
            while(colors[end] != colors[(end - 1 + n) % n] && t < n + k - 1)
            {
                t++;
                end = t % n;
            }
            count +=  max(t - i - k + 1, 0);
            i = t;
             j = i +1;

        }
        return count;
    }
};
